<?php	header('Content-Type: application/json');	include("../conexi.php");	$link =Conectarse();		$respuesta = array();	$contador = 0;	$tablas = array("alumnos" ,  "colegiaturas_por_alumno" , "inscripciones", "pagos", "pagos_detalle", "egresos", "personas_autorizadas", "padres");		foreach ($tablas as $tabla){		$contador++;		$query= "TRUNCATE TABLE $tabla";				$ejecuta = mysqli_query(	$link , $query );		if($ejecuta){			$respuesta["estatus"] = "success";			$respuesta["mensaje"] = "Se borraron $contador tablas";		}		else{			$respuesta["estatus"] = "error";			$respuesta["mensaje"] = "Error al ejecutar $query :".mysqli_error($link);			}		$respuesta["query"][] = $query;	}			print(json_encode($respuesta));?>